package com.demo.tcp;import java.io.IOException;import java.io.InputStream;import java.io.OutputStream;import java.net.ServerSocket;import java.net.Socket;public class Server {    public static void main(String[] args) throws Exception {        ServerSocket ss = new ServerSocket(8877);       while (true) {           Socket s = ss.accept();           new Thread(){               @Override               public void run() {                   InputStream is = null;                   try {                       is = s.getInputStream();                       byte[] bs = new byte[1024];                       int len;                       while ((len = is.read(bs)) != -1) {                           System.out.println(new String(bs, 0, len));                       }                       OutputStream os = s.getOutputStream();                       os.write("正在处理...........".getBytes());                       os.close();                   } catch (IOException e) {                       e.printStackTrace();                   }               }           }.start();       }    }}